sys_variable_value.value:
(function execute(inputs, outputs) {
    var results = [];
    var configParentId = inputs.config_sys_id;

    var gr = new GlideAggregate('x_237456_catalog_0_cat_item_flow_approval_config');
    gr.addQuery('active', true);
    gr.addQuery('cat_item_flow_config', configParentId);
    gr.orderBy('step');
    gr.groupBy('step');
    gr.query();
    while(gr.next()){
       results.push(gr.step.toString());
    }
    
    outputs.results = results;

})(inputs, outputs);

