sys_script_include.script:
var CatItemFlowConfigurationLoader = Class.create();
CatItemFlowConfigurationLoader.prototype = {
    initialize: function() {
    },

    type: 'CatItemFlowConfigurationLoader',

	load: function(req_item){
		var result = {};
		var parentConfig = this._getParentConfig(req_item.cat_item.toString());
		if(parentConfig){
			result.config_sys_id = parentConfig.sys_id.toString();
			result.subflow = parentConfig.sub_flow.toString();
			result.flow_script_include = parentConfig.flow_script_include.toString();
		}
		else{
			result.config_sys_id = '';
			result.subflow = '';
			result.flow_script_include = '';
		}
		return result;
	},
	_getParentConfig: function(cat_item){
		var gr = new GlideRecord('x_237456_catalog_0_cat_item_flow_config');
		gr.addQuery('catalog_item', cat_item);
		gr.addQuery('active', true);
		gr.query();
		if(gr.next())
			return gr;
	}
};
